sys_variable_value.value:
(function execute(inputs, outputs) {
    // Assuming 'response' is the variable that holds the response from the REST Step
    var response = inputs.response; // Replace 'inputs.response' with the actual variable name

    try {
        // Parse the JSON response
        var parsedResponse = JSON.parse(response);

        // Check if the response contains an error
        if (parsedResponse.error) {
            outputs.response = "Error: " + parsedResponse.error.message;
            return;
        }

        // Extract the relevant information from the response
        var choices = parsedResponse.choices;
        if (choices && choices.length > 0) {
            // Assuming we are interested in the first choice's message content
            var messageContent = choices[0].message.content;
            outputs.response = messageContent;
        } else {
            outputs.response = "No valid choices found in response";
        }

    } catch (e) {
        // Handle any parsing errors
        outputs.response = "Error parsing response: " + e.message;
    }
})(inputs, outputs);

