sys_variable_value.value:
(function execute(inputs, outputs) {
    // Preprocess the prompt text to escape JSON special characters and replace line breaks
    var preprocessedprompt = inputs.prompt
        .replace(/\\/g, '\\\\') // Escape backslashes first
        .replace(/"/g, '\\"')   // Escape double quotes
        .replace(/\n/g, '\\n')  // Replace line breaks with \n
        .replace(/\r/g, '\\r')  // Replace carriage returns with \r
        .trim();                // Trim leading/trailing whitespace

    // Pass the preprocessed prompt to the next step
    outputs.preprocessedprompt = preprocessedprompt;
})(inputs, outputs);

